import React, { useEffect, useState } from "react";
import axios from "axios";

const DeliveryBoyPage = () => {
  const backendUrl = "http://localhost:4000/api"; // Backend base URL
  const [orders, setOrders] = useState([]);
  const [status, setStatus] = useState("Fetching location...");
  const [selectedOrder, setSelectedOrder] = useState(null);

  // Fetch orders assigned to the delivery boy
  const fetchOrders = async () => {
    try {
      const response = await axios.get(`${backendUrl}/order/list`);
      if (Array.isArray(response.data)) {
        setOrders(response.data); // Assume the API returns an array of orders
      } else {
        console.error("Unexpected response format:", response.data);
        setOrders([]);
      }
    } catch (error) {
      console.error("Error fetching orders:", error);
    }
  };

  // Post live location to the backend
  const updateLocation = async () => {
    if (!navigator.geolocation) {
      setStatus("Geolocation is not supported by this browser.");
      return;
    }

    navigator.geolocation.getCurrentPosition(
      async (position) => {
        const { latitude, longitude } = position.coords;

        setStatus("Location updated!");

        if (selectedOrder) {
          try {
            await axios.post(
              `${backendUrl}/delivery/update-location`,
              {
                lat: latitude,
                lon: longitude,
                orderId: selectedOrder._id, // Use selected order's ID
              }
            );
          } catch (error) {
            console.error("Error updating location:", error);
            setStatus("Failed to update location.");
          }
        }
      },
      (error) => {
        console.error("Error fetching location:", error);
        setStatus("Failed to fetch location.");
      }
    );
  };

  useEffect(() => {
    fetchOrders();

    // Update location every 15 seconds
    const interval = setInterval(updateLocation, 15000);

    return () => clearInterval(interval); // Cleanup interval on unmount
  }, [selectedOrder]);

  return (
    <div>
      <h2>Delivery Boy Dashboard</h2>
      <p>Status: {status}</p>
      <h3>Orders</h3>
      <ul>
        {orders.length > 0 ? (
          orders.map((order) => (
            <li key={order._id}>
              Order ID: {order._id} - {order.customerAddress}
              <button onClick={() => setSelectedOrder(order)}>Select Order</button>
            </li>
          ))
        ) : (
          <p>No orders available.</p>
        )}
      </ul>
    </div>
  );
};

export default DeliveryBoyPage;
