import React, { useState, useEffect } from "react";
import axios from "axios";

const DeliveryBoyPage = ({ url }) => {
  const [orders, setOrders] = useState([]);
  const [status, setStatus] = useState("Fetching orders...");

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const response = await axios.get(`${url}/api/order/list`);
        console.log("Orders fetched:", response.data);
        if (Array.isArray(response.data.orders)) {
          setOrders(response.data.orders);
          setStatus("Orders fetched successfully.");
        } else {
          setOrders([]);
          setStatus("No orders found.");
        }
      } catch (error) {
        console.error("Error fetching orders:", error);
        setStatus("Failed to fetch orders.");
      }
    };

    fetchOrders();
  }, [url]);

  const updateLocation = async (orderId) => {
    if (!navigator.geolocation) {
      setStatus("Geolocation is not supported by this browser.");
      return;
    }

    navigator.geolocation.getCurrentPosition(
      async (position) => {
        const { latitude, longitude } = position.coords;

        try {
          await axios.post(`${url}/api/delivery/update-location`, {
            orderId,
            lat: latitude,
            lon: longitude,
          });
          setStatus("Location updated successfully.");
        } catch (error) {
          console.error("Error updating location:", error);
          setStatus("Failed to update location.");
        }
      },
      (error) => {
        console.error("Error fetching location:", error);
        setStatus("Failed to fetch location.");
      }
    );
  };

  return (
    <div>
      <h2>Delivery Boy Dashboard</h2>
      <p>Status: {status}</p>
      {Array.isArray(orders) && orders.length > 0 ? (
        orders.map((order) => (
          <div key={order.id}>
            <p>Order ID: {order.id}</p>
            <p>Status: {order.status}</p>
            <button onClick={() => updateLocation(order.id)}>
              Update Location
            </button>
          </div>
        ))
      ) : (
        <p>No active orders.</p>
      )}
    </div>
  );
};

export default DeliveryBoyPage;
