import React, { useEffect, useState } from "react";
import axios from "axios";

const DeliveryBoyPage = () => {
  const [orders, setOrders] = useState([]);
  const [status, setStatus] = useState("Fetching location...");
  const [selectedOrder, setSelectedOrder] = useState(null);

  // Fetch orders assigned to the delivery boy
  const fetchOrders = async () => {
    try {
      const response = await axios.get("http://localhost:4000/api/order/list");
      if (response.data && response.data.success) {
        setOrders(response.data.data); // Access the "data" field
      } else {
        console.error("Unexpected response format:", response.data);
      }
    } catch (error) {
      console.error("Error fetching orders:", error);
    }
  };

  // Post live location to the backend for the selected order
  const updateLocation = async (orderId) => {
    if (!navigator.geolocation) {
      setStatus("Geolocation is not supported by this browser.");
      return;
    }

    navigator.geolocation.getCurrentPosition(
      async (position) => {
        const { latitude, longitude } = position.coords;

        try {
          // Send live location to the backend
          await axios.post("http://localhost:4000/api/delivery/update-location", {
            lat: latitude,
            lon: longitude,
            orderId: orderId, // Selected order ID
          });

          setStatus(`Location updated for Order ID: ${orderId}`);
        } catch (error) {
          console.error("Error updating location:", error);
          setStatus(`Failed to update location for Order ID: ${orderId}`);
        }
      },
      (error) => {
        console.error("Error fetching location:", error);
        setStatus("Failed to fetch location.");
      }
    );
  };

  useEffect(() => {
    fetchOrders();
  }, []);

  return (
    <div>
      <h2>Delivery Boy Dashboard</h2>
      <p>Status: {status}</p>
      <h3>Orders</h3>
      <ul>
        {orders.length > 0 ? (
          orders.map((order) => (
            <li key={order._id}>
              Order ID: {order._id} - {order.address.street}, {order.address.city}
              <button
                onClick={() => {
                  setSelectedOrder(order);
                  updateLocation(order._id); // Update location for the selected order
                }}
              >
                Select Order
              </button>
            </li>
          ))
        ) : (
          <p>No orders available.</p>
        )}
      </ul>
    </div>
  );
};

export default DeliveryBoyPage;
